const data = items[0].json;
let messageContent = data.choices?.[0]?.message?.content || "";

messageContent = messageContent
  .replace(/^```json/i, "")
  .replace(/^```/, "")
  .replace(/```$/, "")
  .trim();

let parsed = {};
try {
  parsed = JSON.parse(messageContent);
} catch (e) {
  const jsonMatch = messageContent.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    try {
      parsed = JSON.parse(jsonMatch[0]);
    } catch (err) {
      parsed = { Subject: "No subject found", Body: messageContent };
    }
  } else {
    parsed = { Subject: "No subject found", Body: messageContent };
  }
}

return [
  {
    json: {
      Subject: parsed.Subject || "Untitled Email",
      Body: parsed.Body || "Not provided"
    }
  }
];

// Parse CSV file from webhook - handles multiple input formats
let csvText = '';

// Method 1: Check for binary data (most common for file uploads)
if (items[0].binary) {
  const binaryKeys = Object.keys(items[0].binary);

  for (const key of binaryKeys) {
    const binaryData = items[0].binary[key];

    if (binaryData && binaryData.data) {
      try {
        const buffer = Buffer.from(binaryData.data, 'base64');
        csvText = buffer.toString('utf-8');
        break;
      } catch (e) {
        console.log('Failed to decode base64:', e);
      }
    }
  }
}

// Method 2: Check body (raw body)
if (!csvText && items[0].body) {
  if (typeof items[0].body === 'string') {
    csvText = items[0].body;
  } else if (Buffer.isBuffer(items[0].body)) {
    csvText = items[0].body.toString('utf-8');
  }
}

// Method 3: Check json body
if (!csvText && items[0].json) {
  if (items[0].json.body && typeof items[0].json.body === 'string') {
    csvText = items[0].json.body;
  } else if (items[0].json.data && typeof items[0].json.data === 'string') {
    csvText = items[0].json.data;
  }
}

if (!csvText || typeof csvText !== 'string') {
  throw new Error(`No valid CSV data found. Received type: ${typeof csvText}. Please upload a CSV file using multipart/form-data.`);
}

// Parse CSV function
function parseCSV(text) {
  const textStr = String(text).trim();

  if (!textStr) {
    throw new Error('CSV text is empty after trimming.');
  }

  const lines = textStr.split('\n');

  if (lines.length < 2) {
    throw new Error('CSV file must have at least a header row and one data row.');
  }

  const headers = lines[0].split(',').map(h => h.trim().replace(/"/g, '').replace(/\r/g, ''));

  return lines.slice(1)
    .filter(line => line.trim())
    .map(line => {
      const values = [];
      let current = '';
      let inQuotes = false;

      for (let char of line) {
        if (char === '"') {
          inQuotes = !inQuotes;
        } else if (char === ',' && !inQuotes) {
          values.push(current.trim().replace(/"/g, '').replace(/\r/g, ''));
          current = '';
        } else if (char !== '\r') {
          current += char;
        }
      }
      values.push(current.trim().replace(/"/g, '').replace(/\r/g, ''));

      const obj = {};
      headers.forEach((header, index) => {
        obj[header] = values[index] || '';
      });
      return obj;
    });
}

try {
  const parsedData = parseCSV(csvText);

  if (parsedData.length === 0) {
    throw new Error('No data rows found in CSV file.');
  }

  return parsedData.map(row => ({ json: row }));
} catch (error) {
  throw new Error(`CSV parsing failed: ${error.message}`);
}

// The Aggregate node output has data in: $input.first().json.data
const aggregatedData = $input.first().json.data || [];

// The data is already flat objects, not nested under 'json'
const allResults = aggregatedData
  .filter(item => item.Email) // Only include items with email addresses
  .map(item => ({
    //Id: item.Id || '',
    Name: item.Name || '',
    Email: item.Email || '',
    Company: item.Company || '',
    // Website: item.Website || '',
    Sector: item.Sector || '',
    Country: item.Country || '',
    Status: item.Status || 'FAILED',
    SentOn: item.SentOn || '',
    //Subject: item.Subject || '',
   // Body: item.Body || ''
  }));

// Count sent vs failed
const sentCount = allResults.filter(r => r.Status === 'SENT').length;
const failedCount = allResults.filter(r => r.Status === 'FAILED').length;

// Return final formatted output
return [
  {
    json: {
      success: true,
      totalProcessed: allResults.length,
      sentSuccessfully: sentCount,
      failed: failedCount,
      results: allResults
    }
  }
];

message.content:
You are an expert marketing copywriter who crafts high-converting, personalized emails for digital campaigns. Always respond with valid JSON containing only 'Subject' and 'Body' fields.

Return only valid JSON like this:
{
  "Subject": "Your subject here",
  "Body": "Your email body here"
}

=Write a marketing email for the following campaign details:

Data Inputs:

- Campaign Name: {{$json.CampaignName || "Product Launch"}}

- Target Audience: Professionals

- Sector: {{$json.Sector || "General"}}

- Goal: {{$json.goal || "Boost brand awareness"}}

- Tone: friendly and persuasive

- Recipient Name: {{$json.Name || "there"}} (employee of {{$json.Company || "Our Brand Partners"}})

- ToCompany: {{$json.Company || "Our Brand Partners"}}

- FromCompany: {{$json.FromCompany}}

- FromCompany Website: {{$json.FromCompanyWebsite || "N/A"}} (add as a clickable hyperlink)

- Country: {{$json.Country || "Global"}}

Email Requirements:

1. Create a catchy subject line (under 10 words).

2. Write a 100–150 word body in HTML format using <p> tags.

3. Use a friendly and persuasive tone tailored to busy professionals.

4. Include one clear call-to-action (CTA) as a clickable link or button text.

5. Do not use placeholders like [Name] — instead, use the provided data or defaults.

6. The final output must be valid JSON only, with this structure:
{
  "Subject": "Your subject here",
  "Body": "Your email body here"
}

7. The output must contain no markdown, no explanations, and no extra text — only the valid JSON object.

8. End with a polite sign-off with {{ $json.FromCompany }}

