// Parse CSV file from webhook - handles multiple input formats
let csvText = '';

// Method 1: Check for binary data (most common for file uploads)
if (items[0].binary) {
  // Check all possible binary property names
  const binaryKeys = Object.keys(items[0].binary);

  for (const key of binaryKeys) {
    const binaryData = items[0].binary[key];

    if (binaryData && binaryData.data) {
      // Base64 encoded data
      try {
        const buffer = Buffer.from(binaryData.data, 'base64');
        csvText = buffer.toString('utf-8');
        break;
      } catch (e) {
        console.log('Failed to decode base64:', e);
      }
    }
  }
}

// Method 2: Check body (raw body)
if (!csvText && items[0].body) {
  if (typeof items[0].body === 'string') {
    csvText = items[0].body;
  } else if (Buffer.isBuffer(items[0].body)) {
    csvText = items[0].body.toString('utf-8');
  }
}

// Method 3: Check json body
if (!csvText && items[0].json) {
  if (items[0].json.body && typeof items[0].json.body === 'string') {
    csvText = items[0].json.body;
  } else if (items[0].json.data && typeof items[0].json.data === 'string') {
    csvText = items[0].json.data;
  }
}

// Validate csvText is a string
if (!csvText || typeof csvText !== 'string') {
  throw new Error(`No valid CSV data found. Received type: ${typeof csvText}. Please upload a CSV file using multipart/form-data.`);
}

// Parse CSV function
function parseCSV(text) {
  // Ensure text is string and trim it
  const textStr = String(text).trim();

  if (!textStr) {
    throw new Error('CSV text is empty after trimming.');
  }

  const lines = textStr.split('\n');

  if (lines.length < 2) {
    throw new Error('CSV file must have at least a header row and one data row.');
  }

  // Parse headers
  const headers = lines[0].split(',').map(h => h.trim().replace(/"/g, '').replace(/\r/g, ''));

  // Parse data rows
  return lines.slice(1)
    .filter(line => line.trim()) // Remove empty lines
    .map(line => {
      const values = [];
      let current = '';
      let inQuotes = false;

      for (let char of line) {
        if (char === '"') {
          inQuotes = !inQuotes;
        } else if (char === ',' && !inQuotes) {
          values.push(current.trim().replace(/"/g, '').replace(/\r/g, ''));
          current = '';
        } else if (char !== '\r') { // Skip carriage returns
          current += char;
        }
      }
      values.push(current.trim().replace(/"/g, '').replace(/\r/g, ''));

      const obj = {};
      headers.forEach((header, index) => {
        obj[header] = values[index] || '';
      });
      return obj;
    });
}

try {
  const parsedData = parseCSV(csvText);

  if (parsedData.length === 0) {
    throw new Error('No data rows found in CSV file.');
  }

  return parsedData.map(row => ({ json: row }));
} catch (error) {
  throw new Error(`CSV parsing failed: ${error.message}`);
}

